a = int(input())b = int(input())while a < 0:    print('Сколько камней в первой куче?')    a = int(input())while b < 0:    print('Сколько камней в первой куче?')    b = int(input())while a != 0 or b != 0:    if a > 1 and b > 1:        k = a - 1        a -= k    elif a > 1 and b == 1:        k = a - 1        a -= k    elif b > 1 and a == 1:        k = b - 1        b -= k    elif a == 1 and b == 1:        k = 1        a -= k    elif a > 0 and b == 0:        p = a        a -= p    elif b > 0 and a == 0:        p = b        b -= p    print(k)    print(a, b)    if a == 0 and b == 0:        print('ИИ Победил!')    else:        n = int(input())        k = int(input())        while k < 1 or n < 0 or n > 2 or n == 1 and k > a or n == 2 and k > b:            print('Попробуй другие числа')            n = int(input())            k = int(input())        if n == 1:            a -= k        else:            b -= k        print(a, b)        if a == 0 and b == 0:            print('ПОЗДРАВЛЯЮ С ПОБЕДОЙ!')